import React,{ Component, useState } from 'react';
import './App.css';
import ReactDOM from 'react-dom';
import IconGroup from  "./IconGroup.jsx";
import { Icon } from 'semantic-ui-react';
import FirstPage from "./FirstPage";

class HeaderComponent extends Component{

    constructor(props){
        super(props);
        this.state ={
            width: 0,
            height: 0
        }
         this.updateDimensions = this.updateDimensions.bind(this);
    }

componentDidMount() {
    window.addEventListener('resize', this.updateDimensions);
  }

   componentWillUnmount() {
    window.removeEventListener('resize', this.updateDimensions);
  }


updateDimensions(){
    this.setState({ width: window.innerWidth, height: window.innerHeight });
  };


  render() {
        return (
        <div class='container2'>
            <div style = {{width : this.state.height}}>
            <img src='/phone_logo.png' class='iconDetails'/><span>&nbsp;&nbsp;&nbsp;&nbsp;9979969696</span>
            <img src='/email_logo.png' class='iconDetailsRight'/><span>&nbsp;&nbsp;&nbsp;&nbsp;info@gmail.com</span>
            <a href="/firstpage" class = 'align-to-right-60-percent'>Register</a>
            <a href="/firstpage" class = 'align-to-right-4-percent'>Login</a>
            </div>
        </div>
        );
    }
}

export default HeaderComponent;
